    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        String itemName = jTextField9.getText();
        DatabaseHelper db = new DatabaseHelper();
        db.createConnection();
        
        ResultSet rs = db.searchDrugs(itemName);
        
        try{
            if( rs != null ){
                rs.next();
                jTextField1.setText( rs.getString(2) );
                jTextField2.setText( rs.getString(1) );
                jTextField3.setText( rs.getString(3) );
                jTextField4.setText( rs.getString(4) );
                jTextField5.setText( rs.getString(5) );
                jTextField6.setText( rs.getString(8) );
                jTextField7.setText( new Integer(rs.getInt(7)).toString() );
                jTextField8.setText( new Float(rs.getFloat(6)).toString() );

            }
            else{
                Notification notification = new Notification();
                notification.setMessage("No drug named " + itemName );
                notification.setVisible(true);
            }
        }
        catch( Exception e ){
            System.out.println( e.getMessage() );
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed

