    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        DatabaseHelper db = new DatabaseHelper();
        db.createConnection();
        
        int status = db.updateDrug(
                jTextField2.getText(), 
                jTextField1.getText(), 
                jTextField3.getText(), 
                jTextField4.getText(), 
                jTextField5.getText(), 
                new Float( jTextField8.getText() ), 
                new Integer( jTextField7.getText() ), 
                jTextField6.getText() );
        
        Notification note = new Notification();
        if( status != 0 ){
            note.setMessage("Record Updated.");
        }
        else{
            note.setMessage("Update failed.");
        }
        note.setVisible(true);        
    }//GEN-LAST:event_jButton4ActionPerformed

